import React, {useState,useRef,useEffect} from 'react'
import { useTable} from 'react-table'
import EventService from '../services/EventService'
import {useNavigate} from 'react-router-dom'
import Navbar from '../components/Navbar';

import BarLoader from "react-spinners/BarLoader";
import SchoolService from '../services/SchoolService';
import PlayerService from '../services/PlayerService';


import Select from 'react-select';
import PlayersReactTable from '../components/reactTables/PlayersReactTable';


const PlayerEnroll = () => {
    const [data,setPlayers]=useState([]);
    const [loading,setLoading] = useState(false);
  const [schoolImages, setSchoolImages] = useState([]);
  const [isSchoolImagesLoaded, setIsSchoolImagesLoaded] = useState(false);
  const optionsSchools = [];
  const optionsCourses = [];
  const [checkedCount, setCheckedCount] = useState(0);
  const [selectedRows, setSelectedRows] = useState([]);
  
  
  const handleCheckboxChange = (event,playerID) => {
    const isChecked = event.target.checked;
    console.log('Checkbox changed:', playerID);
    if (isChecked) {
      setCheckedCount((prevCount) => prevCount + 1);
      var eventID = localStorage.eventID;
      var playerScore = 0;
      setSelectedRows((prevSelectedRows) => [...prevSelectedRows, {playerID, eventID,playerScore}]);      
    } else {
      setCheckedCount((prevCount) => prevCount - 1);
      setSelectedRows((prevSelectedRows) => prevSelectedRows.filter((selectedItem) => selectedItem.playerID !== playerID)
    );}
    
  };
  const navigate  = useNavigate();
    useEffect(()=>{
        if(localStorage.username === undefined){
            navigate("/");
        }
        // get Schools
        SchoolService.getSchools().then((response) => {                 
            for(var i = 0; i < response.data.length; i++) 
            {
                    {schoolImages.push({
                        name: response.data[i].name,
                        image: response.data[i].image,
                    });
                }
            }
            setSchoolImages(schoolImages);
            setIsSchoolImagesLoaded(true); 
            console.log("School Service Called")   
        });
        async function fetchData() {
            setLoading(true);
            console.log("this point was reached")
            await PlayerService.getPlayersBySchool(localStorage.school).then((response) => {           
                setPlayers(response.data);
            });
            setLoading(false);  
            console.log("Hello")
          }
        fetchData();  
        console.log(loading)
        
    },[]);
    const getImage = (schoolName) => {    
        const foundSchool = schoolImages.find(school => schoolName === school.name);
        if (foundSchool) {
        return foundSchool.image;
        }    
        return null;
    };
    const columns = React.useMemo(
        () => [
          {
            Header: 'Division',
            accessor: 'division',        
          },
         
          {
            Header: 'Player ID',
            accessor: 'playerID', 
          },
          {
            Header: 'Name',
            accessor: 'name',
          },
          {
            Header: 'Action',
            Cell: tableProps => (
                
                 
                 <input 
                    type="checkbox" 
                    id={`checkbox-${tableProps.row.index}`} 
                    onChange={(e) => handleCheckboxChange(e, tableProps.row.original.playerID)}
                   // checked={selectedRows.includes(tableProps.row.original.playerID)}
                   />      
                            
                ),      
          }
        ],
        []
    )
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        state,
       
      } = useTable({ columns, data  })
  
  /*const saveEventScoring = async(e) => {
        var eventScorings = [];
        eventScorings.length = 0;
        e.preventDefault();   
        if(isValidForm()){
            setLoading(true);       
            const eventScoring = {
                eventDate: localStorage.eventDate,
                time: localStorage.time,
                golfCourse: localStorage.golfCourse,
                hostSchool,
                teeTimes
            }
            await EventService.saveEvent(event).then((response) => {                     
                localStorage.message = response.data;
                navigate ('/event-sign-up');
            })
            setLoading(false);             
        }
  };
  const isValidForm = () => {
    var valid = true;
        if(eventDate.length < 1){
            inputEventDate.current.style.color = "red";
            valid = false;
        }
        else {
            inputEventDate.current.style.color = "black";
        }

        if(time.length < 1){
            inputTime.current.style.color = "red";
            valid = false;
        }
        else {
            inputTime.current.style.color = "black";
        }
        if(golfCourse.length < 1){
            inputGolfCourse.current.style.color = "red";
            valid = false;
        }
        else {
            inputGolfCourse.current.style.color = "black";
        }
        if(hostSchool.length < 1){
            inputHostSchool.current.style.color = "red";
            valid = false;
        }
        else {
            inputHostSchool.current.style.color = "black";
        }
        if(teeTimes < 1 || teeTimes === null){
            inputTeeTimes.current.style.color = "red";
            valid = false;
        }
        else {
            inputTeeTimes.current.style.color = "black";
        }
        
        return valid;
  }*/
  

PlayerService.getPlayersBySchool(localStorage.school).then((response) => {           
    for(var i = 0; i < response.data.length; i++) 
    {
        optionsCourses.push({
            value: response.data[i].name,
            label: response.data[i].name,
        });
    }
});

  return (
    <div>
        <header>
            <Navbar /> 
        </header>
        <section>    
            <br/> {loading?
                <div style={{marginBottom:0}}>
                    <BarLoader
                        color={"#0d6efd"}
                        loading={loading}        
                        height = {4}
                        width = {200}
                        cssOverride={{marginLeft:'44%'}}          
                    />
                </div>
                :
                <></>}
            <br/>
            <div className = "container" style={{paddingRight:'0.75rem',paddingLeft:'0.75rem',marginLeft: 'auto',marginRight:'auto'}}>
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                        <div>
                            <h2 className = "text-center">Player Enroll</h2>
                            <div className = "card-body">
                                <form action = "" >
                                    <h5>Date: {localStorage.eventDate}</h5>
                                    <br/>
                                    <h5>Time: {localStorage.time}</h5>
                                    <br/>
                                    <h5>Host School:
                                    {isSchoolImagesLoaded  && (
                                            <img src={`data:image/jpeg;base64,${getImage(localStorage.school)}`}  style={{ width: 50, height:50,marginLeft: 10 }} className = 'player1'/>  
                                        )}            
                                    {localStorage.hostSchool}</h5>
                                    <br/>
                                    <h5 >Golf Course: {localStorage.golfCourse}</h5>
                                    <br/>
                                    <hr></hr>
                                    <h5 className = "userdetail">
                                        <span className='name'>
                                            Available Slots: {localStorage.slots}
                                        </span>
                                        <span className = "school">
                                            Players Chosen: {checkedCount}
                                        </span>
                                    </h5>
                                    <br/>
                                    <div   style={{ maxWidth: '99.9%' }}> 
                                        <table {...getTableProps()} className = "table table-striped" style ={{height:20}}>
                                        <thead>
                                            {headerGroups.map(headerGroup => (
                                            <tr {...headerGroup.getHeaderGroupProps()}>
                                                {headerGroup.headers.map(column => (
                                                <th {...column.getHeaderProps()} style = {{background: 'white'}}> {column.render('Header')} </th>
                                                ))}
                                            </tr>
                                            ))}
                                        </thead>
                                        <tbody {...getTableBodyProps()}>
                                            {rows.map(row => {
                                            prepareRow(row)
                                            return (
                                                <tr {...row.getRowProps()}>
                                                {row.cells.map(cell => {
                                                    return (
                                                    <td {...cell.getCellProps()}> {cell.render('Cell')} </td>
                                                    )
                                                })}
                                                </tr>
                                            )
                                            })}
                                        </tbody>
                                        </table>
                                    </div>
                                    <button type="submit" className = "btn btn-primary mb-2 player-right player-left" 
                                            disabled = {loading}
                                            //onClick = {(e) =>saveEvent(e)}
                                            >
                                        Submit
                                    </button>
                                    <button className = "btn btn-primary mb-2 player-right"
                                            disabled = {loading}                                    
                                            onClick = {(e) =>navigate ('/event-sign-up')}>
                                        Cancel
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
        <div>
        <h2>Selected Rows: </h2>
        <ul>
        {selectedRows.map((item, index) => (
          <li key={index}>
            PlayerID: {item.playerID}, EventID: {item.eventID}, Score: {item.playerScore}
          </li>
        ))}
      </ul>
      </div>
    </div>
  )
}

export default PlayerEnroll;
