import React, {useState,useRef,useEffect} from 'react'
import EventService from '../services/EventService'
import {useNavigate} from 'react-router-dom'
import Navbar from '../components/Navbar';
import DatePicker from "react-datepicker";
import TimePicker from 'react-time-picker';
import BarLoader from "react-spinners/BarLoader";
import SchoolService from '../services/SchoolService';
import CourseService from '../services/CourseService';

import Select from 'react-select';


const PlayerEnroll = () => {
  const [eventDate,setEventDate] = useState(new Date());
  const [time,setTime] = useState('')
  const [golfCourse,setGolfCourse] = useState('')
  const [hostSchool,setHostSchool] = useState('')
  const [teeTimes,setTeeTimes] = useState('')
  const [schoolImages, setSchoolImages] = useState([]);
  const [isSchoolImagesLoaded, setIsSchoolImagesLoaded] = useState(false);
  const optionsSchools = [];
  const optionsCourses = [];
  const navigate  = useNavigate();
    useEffect(()=>{
        if(localStorage.username === undefined){
            navigate("/");
        }
        // get Schools
        SchoolService.getSchools().then((response) => {                 
            for(var i = 0; i < response.data.length; i++) 
            {
                    {schoolImages.push({
                        name: response.data[i].name,
                        image: response.data[i].image,
                    });
                }
            }
            setSchoolImages(schoolImages);
            setIsSchoolImagesLoaded(true);    
        });        
    });
    const getImage = (schoolName) => {    
        console.log(schoolImages);
        const foundSchool = schoolImages.find(school => schoolName === school.name);
        if (foundSchool) {
        return foundSchool.image;
        }    
        return null;
    };

  
    const inputEventDate = useRef();  
    const inputTime = useRef();
  const inputGolfCourse = useRef();
  const inputHostSchool = useRef();
  const inputTeeTimes = useRef();
  
  
  const [loading,setLoading] = useState(false);
  const saveEvent = async(e) => {
        e.preventDefault();   
        if(isValidForm()){
            setLoading(true);
            const convEventDate = eventDate.toLocaleDateString();         
            const event = {
                eventDate: convEventDate,
                time,
                golfCourse,
                hostSchool,
                teeTimes
            }
            await EventService.saveEvent(event).then((response) => {                     
                localStorage.message = response.data;
                navigate ('/event-sign-up');
            })
            setLoading(false);             
        }
  };
  const isValidForm = () => {
    var valid = true;
        if(eventDate.length < 1){
            inputEventDate.current.style.color = "red";
            valid = false;
        }
        else {
            inputEventDate.current.style.color = "black";
        }

        if(time.length < 1){
            inputTime.current.style.color = "red";
            valid = false;
        }
        else {
            inputTime.current.style.color = "black";
        }
        if(golfCourse.length < 1){
            inputGolfCourse.current.style.color = "red";
            valid = false;
        }
        else {
            inputGolfCourse.current.style.color = "black";
        }
        if(hostSchool.length < 1){
            inputHostSchool.current.style.color = "red";
            valid = false;
        }
        else {
            inputHostSchool.current.style.color = "black";
        }
        if(teeTimes < 1 || teeTimes === null){
            inputTeeTimes.current.style.color = "red";
            valid = false;
        }
        else {
            inputTeeTimes.current.style.color = "black";
        }
        
        return valid;
  }
  

CourseService.getCourses().then((response) => {           
    for(var i = 0; i < response.data.length; i++) 
    {
        optionsCourses.push({
            value: response.data[i].name,
            label: response.data[i].name,
        });
    }
});

  return (
    <div>
    <header>
        <Navbar /> 
     </header>
        <section>
        
        
           
     <br/> {loading?
                <div style={{marginBottom:0}}>
                    
                    <BarLoader
                        color={"#0d6efd"}
                        loading={loading}        
                        height = {4}
                        width = {200}
                        cssOverride={{marginLeft:'44%'}}          
                    />
                </div>
                :
                <></>    
            }<br/>
    <div className = "container" style={{paddingRight:'0.75rem',paddingLeft:'0.75rem',marginLeft: 'auto',marginRight:'auto'}}>
        <div className = "row">
            <div className = "card col-md-6 offset-md-3 offset-md-3">
                <div>
                    <h2 className = "text-center">Player Enroll</h2>
                    <div className = "card-body">
                        <form action = "" >
                            <h5>Date: {localStorage.eventDate}</h5>
                            <br/>
                            <h5>Time: {localStorage.time}</h5>
                            <br/>
                            <h5>Host School:
                                <img src={`data:image/jpeg;base64,${getImage(localStorage.hostSchool)}`}  style={{ width: 30, height:30,marginRight: 10 }} className = 'player1'/>   
                             {localStorage.hostSchool}</h5>
                             <br/>
                             <h5 >Golf Course: {localStorage.golfCourse}</h5>
                             <br/>
                             <span>
                             <h5>Available Slots: {localStorage.slots}</h5>
                             <h5 className = "school">Players Chosen: {localStorage.slots}</h5>
                             </span>
                             <br/>


                            <button type="submit" className = "btn btn-primary mb-2 player-right player-left" 
                                     disabled = {loading}
                                     onClick = {(e) =>saveEvent(e)}>
                                Submit
                            </button>
                            <button className = "btn btn-primary mb-2 player-right"
                                    disabled = {loading}                                    
                                    onClick = {(e) =>navigate ('/event-sign-up')}>
                                Cancel
                            </button>
                        </form>

                    </div>
                </div>

            </div>

        </div>
        
    </div>
    </section>
    </div>
  )
}

export default PlayerEnroll;
